SELECT CONCAT('https://www.edge-ai-vision.com/', YEAR(post_date), '/', MONTH(post_date), '/', post_name), (length(post_content )-length(replace(post_content ,'embedded-vision','')))/15+1 FROM wp_posts WHERE ID IN (SELECT CASE WHEN post_parent=0 THEN ID ELSE post_parent END FROM wp_posts WHERE post_content LIKE '%embedded-vision%') AND post_status='publish';
